mapper.update:
UPDATE apply_order
SET status      = #{status},
    modify_time = NOW()
WHERE apply_order_id = #{applyOrderId}
  and status = #{oldStatus}

mapper.select:
SELECT apply_order_id, user_name, status, certificate_type, certificate_id, phone_number, email, create_time, modify_time FROM apply_order WHERE status IN
#{status}
order by create_time limit 50;
